<?php Class Manage extends Auth_Controller  { 	var $data = array();	//use `Auth_Controller` if you want the page to be validated if the user is logged in or not, if you want to disable this then use `CI_Controller` instead	public  function __construct(){		parent::__construct();		$this->load->model('manage_model');		$this->load->helper('url');		$this->load->library('session');  		$this->load->helper(array('form', 'url'));  		//$this->has_permission(178);		$this->data = $this->get_privs();		$this->load->library('MY_utils');	}		public function calldate(){		$pdata = $this->input->post();		$this->set_header_data(PROJECT_NAME,'Manage Calldate');		$crms = $this->my_utils->get_all_crms();		$newCrms = array();		foreach($crms as $details){			$newCrms[$details['id']] = $details['name'];		}				$data['start_calldate'] = isset($pdata['start_date']) ? $pdata['start_calldate'] : date('Y-m-d');		$data['end_calldate'] = isset($pdata['end_calldate']) ? $pdata['end_calldate'] : date('Y-m-d');		$data['crms'] = $newCrms;		//get CRM		$this->load->view('manage/calldate',$data);		$this->load->view('templates/footer'); 	    }		public function search_customer(){		$data = $this->input->post();		$data['crmDetails'] = $this->my_utils->get_crm_by_id($data['crm_id']);		$data['customers'] = $this->manage_model->search_customer();		$data['lu_arr'] = $this->manage_model->getCampaignLookUp();		$this->load->view('manage/customer_list',$data);	}		public function update_main($crm_id){		$this->manage_model->update_main($crm_id);	}	}?>